import React, { useEffect, useState } from "react";
import jwtdecode from "jwt-decode";
import "./Todolist.css";
import AddToDo from "../components/AddToDo";
import Ongoing from "../components/Ongoing";
import api from "../components/config/Api";

const Todolist = () => {
  const token = localStorage.getItem("token");
  const jwt = jwtdecode(token);
  const [dataUser, setDataUser] = useState([]);
  const getdate= new Date().getHours()
  const [getHour,setGetHour]=useState("")
  console.log(getHour);
  useEffect(() => {
    api.get(`get/${jwt.id}`).then((res) => {
      setDataUser(res.data.data.Notes);
    }) 
    if(getdate > 3 && getdate < 12){
      setGetHour("Selamat Pagi")
    } else if( getdate > 12 && getdate<15 ){
      setGetHour("Selamat Siang")
    } else if (getdate>15 && getdate<18){
      setGetHour("Selamat Sore")
    } else if(getdate >18 && getdate <3){
      setGetHour("Selamat Malem")
    }
  }, []);

  return (
    <div className="salam">
      <h1>{getHour}, {jwt.firstname}</h1>

      <AddToDo id={jwt.id} />
      <h2>Ongoing</h2>
      {dataUser.map(el=>
      el.isPriority===false &&  (
            <Ongoing key={el.id} todo={el.note} id={el.id} isPriority={el.isPriority} /> 
          ))}
      <h2>Complited</h2>
      {dataUser.map(el=>
      el.isPriority===true &&  (
            <Ongoing key={el.id} todo={el.note} id={el.id} checkedComplite={true} coret="coret"/>
            
          ))
          
          }
    </div>
  );
};

export default Todolist;
